    @fact decoration(cancelplus(Interval(∅, trv), Interval(entireinterval(Float64), def))) --> decoration(Interval(entireinterval(Float64), trv))
    @fact cancelplus(Interval(BareInterval(-1.0, 5.0), dac), Interval(BareInterval(1.0, Inf), dac)) --> Interval(entireinterval(Float64), trv)
    @fact decoration(cancelplus(Interval(BareInterval(-1.0, 5.0), dac), Interval(BareInterval(1.0, Inf), dac))) --> decoration(Interval(entireinterval(Float64), trv))
